<new_text>---
import BaseLayout from "../layouts/BaseLayout.astro";
import BlogPost from "../components/BlogPost.astro";

const pageTitle = "My Blog";
const allPosts = Object.values(
    import.meta.glob("./posts/*.md", { eager: true }),
);

// Sort posts by date (newest first)
const sortedPosts = allPosts.sort((a, b) => {
    return new Date(b.frontmatter.pubDate) - new Date(a.frontmatter.pubDate);
});
---

<BaseLayout pageTitle={pageTitle}>
    <div class="blog-container">
        <h1 class="page-title">{pageTitle}</h1>
        <p class="intro-text">Welcome to my blog! Here you'll find all my latest thoughts and articles.</p>

        <div class="post-grid">
            {sortedPosts.map((post: any) => (
                <BlogPost
                    url={post.url}
                    title={post.frontmatter.title}
                    date={post.frontmatter.pubDate.split("T")[0]}
                />
            ))}
        </div>
    </div>
</BaseLayout>

<style>
    .blog-container {
        max-width: 900px;
        margin: 0 auto;
        padding: 0 1.5rem;
    }

    .page-title {
        font-size: 2.5rem;
        margin-bottom: 1rem;
        color: #333;
    }

    .intro-text {
        font-size: 1.2rem;
        margin-bottom: 3rem;
        color: #666;
        line-height: 1.6;
    }

    .post-grid {
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
        gap: 2rem;
    }

    @media (max-width: 768px) {
        .post-grid {
            grid-template-columns: 1fr;
        }

        .page-title {
            font-size: 2rem;
        }
    }
</style></new_text>
</edit>
</patch>
