import HeaderLink from "./HeaderLink.astro";
import { SITE_TITLE } from "../consts";
---

<header class="py-4 px-6 bg-white shadow-md">
    <nav class="flex items-center justify-between">
        <h2 class="text-lg font-medium">
            <a href="/" class="no-underline hover:text-gray-700 transition-colors">{SITE_TITLE}</a>
        </h2>
        <div class="internal-links flex space-x-4">
            <HeaderLink href="/" class="py-2 px-3 text-gray-800 border-b-2 border-transparent hover:border-blue-500 transition-colors">Home</HeaderLink>
            <HeaderLink href="/blog" class="py-2 px-3 text-gray-800 border-b-2 border-transparent hover:border-blue-500 transition-colors">Blog</HeaderLink>
            <HeaderLink href="/about" class="py-2 px-3 text-gray-800 border-b-2 border-transparent hover:border-blue-500 transition-colors">About</HeaderLink>
        </div>
        <div class="social-links hidden md:flex">
            <!-- Social links can be added here -->
        </div>
    </nav>
</header>
